import React, {useState} from 'react';


const ToDoItem = (props) => {

  const handleClick = (event) => {
    setIsDone((prevValue) => {
      return !prevValue;
    })
  };

  return (
  <div onClick={handleClick}>
    <li style={{ textDecoration: isDone ? "line-through" : "none"}}>{props.text}</li>
  </div>
  )
};



export default ToDoItem;
